import React, {useState} from 'react'

// GUI
import ForegroundGUI from '../components/gui/layers/foregroundGUI'
import BackgroundGUI from '../components/gui/layers/backgroundGUI'
import Page from "../components/page"

export default function Landing() {

  // state management
  const [blur, setBlur] = useState(false);
  function routeState() {
    setBlur(!blur);
  }

  // theme is in format [<background>, <foreground/border>]
  // this can be automated through component
  const THEME = [3,4];
  return (
    // <Page 
    //   backgroundColor={3}
    //   foregroundColor={4}
    //   backgroundElements={["Wallpaper", "Headline"]}
    //   foregroundElements={[["Menu", "Social"]]}

    // />
    <div>
      {Body(THEME, blur)}
      <BackgroundGUI 
        theme={THEME}
        blur={blur}
        elements={["Wallpaper", "Headline"]}
      />

      <ForegroundGUI 
        theme={THEME}
        routeState={routeState}
        elements={["Menu", "Social"]}
      />
    </div>
  )
}
