import React, {useState} from 'react'

export default function MenuButton(props) {
  // initial state
  const [visible, setVisible] = useState(false);

  return (
    <div className="MenuButton" onClick={() => setVisible(!visible)}>
      <div className={`Strip Strip_1 Background_${props.colorID}`} />
      <div className={`Strip Strip_2 Background_${props.colorID}`} />
      <div className={`Strip Strip_3 Background_${props.colorID}`} />
      <div className={`Strip Strip_4 Background_${props.colorID}`} />
    </div>
  )
}

// render the strips
function RenderStrips(visible) {
  let state = visible ? "" : "_X";
  let strips = [];
  const MAX_STRIPS = 4;
  const COLOR_ID = props.colorID;
  for(var index = 1; index < MAX_STRIPS; index++) {
    strips.push(<div className={`Strip Strip_${index}${state} Background_${COLOR_ID}`} />);
  }
  return strips
}
