import React, {useState, useEffect} from 'react'

import MenuButton from "./menuButton";

export default function Menu(props) {
  // initial state
  const [visible, setVisible] = useState(false);
  useEffect(() => (
    ShowMenu(visible, props)
  ));

  return (
    <div className="Menu">
      <MenuButton colorID={props.colorID[1]} onClick={() => setVisible(!visible)} />
      {ShowMenu(visible, props)}
    </div>
  )
}

function ShowMenu(isVisible, props) {
  var className = isVisible ? "MenuWrapper" : "MenuWrapper Hidden"
  return (
    <div className={className}>
      <div className={`MenuOverlay Background_${props.colorID[0]}`} />
      <div className="MenuContent"> 

      </div>
    </div>
  )
}
